package ex05;

//operator 연산자
public class OperEx01 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("1 + 1 = "+(1+1)); //덧셈연산자
		System.out.println("2 - 1 = "+(2-1)); //뺼셈연산자
		System.out.println("3 * 5 = "+(3*5)); //곱셉연산자
		System.out.println("10 / 3 = "+ (10/3)); //나누기 연산자(몫만 출력)
		System.out.println("10 % 3 = "+(10%3)); //나머지 연산자
		
		System.out.println("===============");
		System.out.println("10 / 3.0 = "+(10/3.0)); 
	}
}
