package ex05;

public class OperEx03 {
	//논리연산자
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("true && false : " + (true&&false));	//모두가 참(true)일때만 참(true)반환
		System.out.println("true && true : " + (true&&true));
		System.out.println("================================");
		System.out.println("false || false : " + (false||false)); //둘 중 하나라도 참(true)이라면 참(true)반환, 둘다 false일때 false반환
	

	}
}
